import React, { Component, useState } from "react";
import {
  ComposableMap,
  Geographies,
  Geography,
  ZoomableGroup,
} from "react-simple-maps";
import ReactTooltip from "react-tooltip";
// import { useSpring, animated } from "react-spring";
import "./Map.css";
import { connect } from "../api/index.js";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

//NOTE: For API, please see src/api/index.js;

class GameMap extends Component {
  constructor() {
    super();
    connect();
    const naam = "Shashwat";
  }

  render() {
    return <SideBar props="Shashwat" />;
  }
}

// function MapDisplay() {
//   return (
//     <div>
//       <SideBar />
//     </div>
//   );
// }

//Variables for each country to display for Sidebar
// var country;
// var pop_est;
// var gdp;
// var subrg;
// var continent;
// var displayCountryDetails = false;
// var troopsInLand;
// var yourland;

function CountryDetails(props) {
  return (
    <div>
      <h2>Spy Report On {props.country}:</h2>
      <h3>Population: {props.pop_est}</h3>
      <h3>GDP (PPP): {props.gdp}</h3>
      <h3>Subregion: {props.subrg}</h3>
      <h3>Continent: {props.continent}</h3>
    </div>
  );
}

function SideBar() {
  const [state, setState] = useState("");
  const [spydata, setSpydata] = useState({
    Country: "",
    Population: "",
    GDP: "",
    Subregion: "",
    Continent: "",
    Display: false,
  });

  return (
    <div>
      <div className="map-sidebar-wrapper">
        <div className="map-sidebar-info-wrapper">
          <div>
            <h1>Welcome Commander!</h1>
          </div>
          <p>
            This is your war control room. Help us attain victory over our
            enemies. The Gods are on our side!
          </p>
          {/* {displayCountryDetails ? <CountryDetails /> : null} */}
        </div>
      </div>
      <MapSettings setTooltipContent={setState} setSpydata={setSpydata} />
      {spydata.display && <CountryDetails props={spydata} />}
      <ReactTooltip>{state}</ReactTooltip>
    </div>
  );
}

const getnum = (num) => {
  var num = Math.round(num);
  if (num > Math.pow(10, 12)) {
    num = num / Math.pow(10, 12) + " Trillion";
  } else if (num > Math.pow(10, 9)) {
    num = num / Math.pow(10, 9) + " Billion";
  } else if (num > Math.pow(10, 6)) {
    num = num / Math.pow(10, 6) + " Million";
  }
  return num;
};

const MapSettings = ({ setTooltipContent, setSpydata }) => {
  return (
    <div className="map-wrapper">
      <ComposableMap data-tip="">
        <ZoomableGroup>
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill="#AAA"
                  stroke="#FFF"
                  onMouseEnter={() => {
                    const {
                      NAME,
                      POP_EST,
                      GDP_MD_EST,
                      SUBREGION,
                      CONTINENT,
                    } = geo.properties;
                    setTooltipContent(
                      `${NAME} - $${getnum(GDP_MD_EST * Math.pow(10, 6))}`
                    );
                    const name = NAME;
                    const pop_est = getnum(POP_EST);
                    const gdp = getnum(GDP_MD_EST * Math.pow(10, 6));
                    const subrg = SUBREGION;
                    const continent = CONTINENT;
                    const display = true;
                    setSpydata(name, pop_est, gdp, subrg, continent, display);
                  }}
                  onMouseLeave={() => {
                    setTooltipContent("");
                    const display = false;
                    setSpydata("", "", "", "", "", display);
                  }}
                  style={{
                    default: {
                      fill: "#D6D6DA",
                      outline: "none",
                    },
                    hover: {
                      fill: "#F53",
                      outline: "none",
                    },
                    pressed: {
                      fill: "#D6D6DA",
                      outline: "none",
                    },
                  }}
                />
              ))
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    </div>
  );
};

export default GameMap;
