import React, { Component, useEffect, useRef } from "react";
import videoSource from "../media/HomeBackgroundVideo.mp4";
import {
  Card,
  Container,
  Paper,
  Typography,
  AppBar,
  Toolbar,
  Box,
  makeStyles,
  createMuiTheme,
  Button,
} from "@material-ui/core";
import "./Home.css";

function Credits() {
  return (
    <div className="credits">
      <Box color="white">
        <Typography variant="body2" color="gray" align="center">
          {"Copyright Â© "}
          {new Date().getFullYear()}
          {"."}
        </Typography>
      </Box>
    </div>
  );
}

function Title() {
  return (
    <div className="title-head">
      <Box align="center" color="white">
        <Typography variant="h2">CONQUER V2.0</Typography>
      </Box>
      <Box color="white" align="center">
        <Typography variant="subtitle1" align="center">
          by Shashwat Kansal and Akshat Tripathi
        </Typography>
      </Box>
    </div>
  );
}

function StartGameBox(props) {
  return (
    <div className="gamebox-container">
      <h3 className="gamebox-title">Join the game!</h3>
      <form classname="gamebox-form">
        <ul>
          <div classname="game-enter-data-signed-out">
            <li>
              <input
                type="text"
                id="username"
                name="username"
                value="Choose a cool username!"
              />
            </li>
            <li className="input">
              <input type="text" id="username" name="username" />
            </li>
          </div>
          <br></br>
          <li className="button">
            <Button variant="outlined" color="secondary">
              Commence War
            </Button>
          </li>
        </ul>
      </form>
    </div>
  );
}

function Video() {
  useEffect(() => {
    attemptPlay();
  }, []);

  const videoEl = useRef(null);

  const attemptPlay = () => {
    videoEl &&
      videoEl.current &&
      videoEl.current.play().catch((error) => {
        console.error("Error attempting to play", error);
      });
  };

  return (
    <div className="video">
      <video loop muted autoPlay className="home-video" id="home-video">
        <source
          playsInline
          src={videoSource}
          type="video/mp4"
          alt="This is Sparta!"
        />
        What kind of browser version are you on... Your browser unfortunately
        does not yet support the video tag!
      </video>
    </div>
  );
}

export default class Home extends Component {
  render() {
    return (
      <div className="home-page">
        <Video />
        <Title />
        <StartGameBox />
        <Credits />
      </div>
    );
  }
}
