import React, { memo } from 'react';
import { ZoomableGroup, ComposableMap, Geographies, Geography, useZoomPan } from 'react-simple-maps';
import ReactTooltip from 'react-tooltip';

const geoUrl = 'https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json';

const rounded = (num) => {
	if (num > 1000000000) {
		return Math.round(num / 100000000) / 10 + 'Bcn';
	} else if (num > 1000000) {
		return Math.round(num / 100000) / 10 + 'M';
	} else {
		return Math.round(num / 100) / 10 + 'K';
	}
};

const MapChart = ({ setTooltipContent }) => {
	return (
		<div>
			<ComposableMap data-tip="" projectionConfig={{ scale: 200 }}>
				<CustomZoomableGroup center={[ 0, 0 ]}>
					{/* <ZoomableGroup> */}
					<Geographies geography={geoUrl}>
						{({ geographies }) =>
							geographies.map((geo) => (
								<Geography
									key={geo.rsmKey}
									geography={geo}
									onMouseEnter={() => {
										const { NAME, POP_EST } = geo.properties;
										setTooltipContent(`${NAME} — ${rounded(POP_EST)}`);
									}}
									onMouseLeave={() => {
										setTooltipContent('');
									}}
									style={{
										default: {
											fill: '#D6D6DA',
											outline: 'none'
										},
										hover: {
											fill: '#F53',
											outline: 'none'
										},
										pressed: {
											fill: '#D6D6DA',
											outline: 'none'
										}
									}}
								/>
							))}
					</Geographies>
					{/* </ZoomableGroup> */}
				</CustomZoomableGroup>
			</ComposableMap>
		</div>
	);
};

const CustomZoomableGroup = ({ children, ...restProps }) => {
	const { mapRef, transformString, position } = useZoomPan(restProps);
	return (
		// <div className="zoomable-group">
		<g ref={mapRef}>
			<rect fill="transparent" />
			<g transform={transformString}>{children(position)}</g>
		</g>
		// </div>
	);
};

function VectorMap() {
	const [ content, setContent ] = React.useState('');
	return (
		<div>
			<MapChart setTooltipContent={setContent} />
			<ReactTooltip>{content}</ReactTooltip>
		</div>
	);
}

export default memo(VectorMap);
