import React, { Component, useEffect, useRef } from "react";
import videoSource from "../media/HomeBackgroundVideo.mp4";
import {
  Card,
  Container,
  Paper,
  Typography,
  AppBar,
  Toolbar,
  Box,
  makeStyles,
} from "@material-ui/core";
import "./Home.css";

function Credits() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      {new Date().getFullYear()}
      {"."}
      {"CONQUER V2.0 by Shashwat Kansal and Akshat Tripathi"}
    </Typography>
  );
}

function Title() {
  return (
    <AppBar position="absolute" color="default">
      <Toolbar>
        <Typography variant="h6" color="inherit" align="center">
          CONQUER V2.0
        </Typography>
      </Toolbar>
    </AppBar>
  );
}

function StartGameBox() {
  const classes = useStyles();

  return (
    <div className="title-container">
      <Box>
        <Paper elevation={5} variant="elevation" classname={classes.paper}>
          <Typography component="h1" variant="h4" align="center">
            Join a game now!
          </Typography>
        </Paper>
      </Box>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    padding: theme.spacing(2),
  },
}));

function Video() {
  useEffect(() => {
    attemptPlay();
  }, []);

  const videoEl = useRef(null);

  const attemptPlay = () => {
    videoEl &&
      videoEl.current &&
      videoEl.current.play().catch((error) => {
        console.error("Error attempting to play", error);
      });
  };

  return (
    <video loop muted autoPlay className="home-video">
      <source
        playsInline
        src={videoSource}
        type="video/mp4"
        alt="This is Sparta!"
      />
      What kind of browser version are you on... Your browser unfortunately does
      not yet support the video tag!
    </video>
  );
}

export default class Home extends Component {
  render() {
    return (
      <div className="home-page">
        <div className="video">
          <Video />
        </div>
        <div>
          <Title />
        </div>
        <StartGameBox />
        <div z-index="1"></div>
        <Credits />
      </div>
    );
  }
}
