import React, { Component, useEffect, useRef } from "react";
import videoSource from "../media/HomeBackgroundVideo.mp4";
import {
  Typography,
  Box,
  makeStyles,
  Button,
  TextField,
  MenuItem,
  FormControl,
} from "@material-ui/core";
import "./Home.css";

function Credits() {
  return (
    <div className="credits">
      <Box color="white">
        <Typography variant="body2" color="gray" align="center">
          {"Copyright Â© "}
          {new Date().getFullYear()}
          {"."}
        </Typography>
      </Box>
    </div>
  );
}

function Title() {
  return (
    <div className="title-head">
      <Box align="center" color="white">
        <Typography variant="h2">CONQUER V2.0</Typography>
      </Box>
      <Box color="white" align="center">
        <Typography variant="subtitle1" align="center">
          by Shashwat Kansal and Akshat Tripathi
        </Typography>
      </Box>
    </div>
  );
}

const gamemodes = [
  {
    label: "World Conquest - 2025",
    value: "WC",
  },
  {
    label: "World War I - 1914",
    value: "EW",
  },
  {
    label: "American Independence War - 1776",
    value: "ACW",
  },
  {
    label: "Genghis Khan's Expedition - 1333",
    value: "GKE",
  },
  {
    label: "The Mahabharata - 3200BCE",
    value: "MB",
  },
];

const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const onSubmit = async (values) => {
  await sleep(300);
  window.alert(JSON.stringify(values, 0, 2));
};

function StartGameBox(props) {
  //Collect form information
  const state = {
    username: "Genghis Khan",
    gamemode: "WC",
    noOfPlayers: 6,
  };

  //List the gamemode options
  const [war, setWar] = React.useState("World Conquest 2025");

  const handleChange = (event) => {
    setWar(event.target.value);
  };

  return (
    <div className="gamebox-container">
      <h3 className="gamebox-title">Join the game!</h3>
      <form classname="gamebox-form" onSubmit={onSubmit}>
        <div>
          <FormControl variant="filled" fullWidth={true}>
            <label>Username</label>
            <TextField
              required
              id="outlined-required"
              label="Required"
              defaultValue="Genghis Khan"
              variant="filled"
              align="center"
              color="white"
            />
          </FormControl>
        </div>
        <div>
          <FormControl variant="filled" fullWidth={true}>
            <label>Gamemode</label>
            <TextField
              id="standard-select-currency"
              select
              label="Select"
              value={war}
              onChange={handleChange}
              helperText="Choose War Event"
              variant="filled"
            >
              {gamemodes.map((option) => (
                <MenuItem key={option.value} value={option.value}>
                  {option.label}
                </MenuItem>
              ))}
            </TextField>
          </FormControl>
        </div>

        <Button variant="outlined" color="secondary" align="center">
          Commence War
        </Button>
      </form>
    </div>
  );
}

function Video() {
  useEffect(() => {
    attemptPlay();
  }, []);

  const videoEl = useRef(null);

  const attemptPlay = () => {
    videoEl &&
      videoEl.current &&
      videoEl.current.play().catch((error) => {
        console.error("Error attempting to play", error);
      });
  };

  return (
    <div className="video">
      <video loop muted autoPlay className="home-video" id="home-video">
        <source
          playsInline
          src={videoSource}
          type="video/mp4"
          alt="This is Sparta!"
        />
        What kind of browser version are you on... Your browser unfortunately
        does not yet support the video tag!
      </video>
    </div>
  );
}

export default class Home extends Component {
  render() {
    return (
      <div className="home-page">
        <Video />
        <Title />
        <StartGameBox />
        <Credits />
      </div>
    );
  }
}
