import React, { Component, useEffect, useRef } from "react";
import videoSource from "../media/HomeBackgroundVideo.mp4";
import {
  Typography,
  Box,
  makeStyles,
  Button,
  TextField,
  Paper,
  Container,
  Grid,
  Link,
} from "@material-ui/core";
import "./Home.css";
import { useForm } from "react-hook-form";

function Credits() {
  return (
    <div className="credits">
      <Box color="white">
        <Typography variant="body2" color="gray" align="center">
          {"Copyright © "}
          {new Date().getFullYear()}
          {"."}
        </Typography>
      </Box>
    </div>
  );
}

function Title() {
  const useStyles = makeStyles({
    root: {
      background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
      borderRadius: 5,
      border: 1,
      height: "8%",
      paddingTop: "0%",
      boxShadow: "0 5px 7px 5px rgba(255, 105, 135, .3)",
    },
  });

  const classes = useStyles();

  return (
    <Container maxWidthLg elevation={3} classes={{ root: classes.root }}>
      {/* <div className="title-head"> */}
      <Box align="center" color="white">
        <Typography variant="h2">CONQUER V2.0</Typography>
        <Box color="white" align="center">
          {/* <Typography variant="subtitle1" align="center">
            by Imperium Games
          </Typography> */}
        </Box>
      </Box>
      {/* </div> */}
    </Container>
  );
}

function PermaFooter() {
  const useStyles = makeStyles((theme) => ({
    footer: {
      backgroundColor: theme.palette.background.paper,
      paddingBottom: "0%",
      paddingLeft: "0%",
      paddingRight: "0%",
      paddingTop: "0%",
    },
  }));

  const classes = useStyles();

  return (
    <Component component="main" maxWidth="lg">
      <Container className={classes.footer} position="fixed">
        <Box>
          {/* <Typography variant="h6" align="center" gutterBottom>
        Conquer V2.0
      </Typography> */}
          <Typography
            variant="subtitle1"
            align="center"
            color="textSecondary"
            component="p"
          >
            Developed by Imperium Games
          </Typography>
          <Copyright />
        </Box>
      </Container>
    </Component>
  );
}

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

function fancyTitle() {
  return (
    <>
      <div className="rainbow-text">
        <div className="title-text">
          <span className="letters">C</span>
          <span className="letters">O</span>
          <span className="letters">N</span>
          <span className="letters">Q</span>
          <span className="letters">U</span>
          <span className="letters">E</span>
          <span className="letters">R </span>
          <span className="letters">2.0</span>
        </div>
      </div>
    </>
  );
}

const gamemodes = [
  {
    label: "World Conquest - 2025",
    value: "WC",
  },
  {
    label: "World War I - 1914",
    value: "EW",
  },
  {
    label: "American War of Independence- 1776",
    value: "ACW",
  },
  {
    label: "Genghis Khan's Expedition - 1333",
    value: "GKE",
  },
  {
    label: "The Mahabharata - 3200BCE",
    value: "MB",
  },
];

var username;
function StartGameBox(props) {
  //Collect form information

  //List the gamemode options

  const handleUsername = () => {
    username = document.getElementById("ign");
  };

  return (
    // <div className="wrapper">
    //   <div className="form-wrapper">
    <div>
      <h3 className="gamebox-title">Join the game!</h3>

      <form action="/join" method="POST">
        <div className="username">
          <TextField
            type="text"
            id="ign"
            placeholder="Username"
            name="username"
            required
            onChange={handleUsername}
          />
          {/* <input
            type="text"
            id="ign"
            placeholder="Username"
            name="username"
            required
            onChange={handleUsername}
          ></input> */}
        </div>
        <div>
          <TextField
            type="password"
            placeholder="Password"
            name="password"
            required
          />
          {/* <input
            type="password"
            placeholder="Password"
            name="password"
            required
          ></input> */}
        </div>
        <div>
          <TextField type="text" placeholder="Game Id" name="id" required />
          {/* <input type="text" placeholder="Game Id" name="id"></input> */}
        </div>
        <Button
          type="submit"
          name="submit"
          value="join"
          variant="contained"
          color="primary"
        >
          Join Game
        </Button>
        {/* <input type="submit" name="submit" value="join"></input> */}
      </form>

      <form action="/create" method="POST">
        <select className="gamemode" name="type" required>
          <option value="realtime">Realtime game</option>
        </select>
        <div>
          <TextField
            type="text"
            placeholder="Username"
            name="username"
            required
            variant="outlined"
          />
          {/* <input
              type="text"
              placeholder="Username"
              name="username"
              required
            ></input> */}
        </div>
        <div>
          <TextField
            type="password"
            placeholder="Password"
            name="password"
            required
            variant="outlined"
          />
          {/* <input
              type="password"
              placeholder="Password"
              name="password"
              required
            ></input> */}
        </div>
        <div>
          <TextField
            className="noOfPlayers"
            type="number"
            placeholder="maxPlayers"
            name="maxPlayers"
            required
            variant="outlined"
          />
          {/* <input
              className="noOfPlayers"
              type="number"
              placeholder="maxPlayers"
              name="maxPlayers"
              required
            ></input> */}
        </div>
        <div>
          <TextField
            type="number"
            placeholder="startingTroops"
            name="startingTroops"
            required
            variant="outlined"
          />
          {/* <input
              type="number"
              placeholder="startingTroops"
              name="startingTroops"
              required
            ></input> */}
        </div>
        <div>
          <TextField
            type="number"
            placeholder="startingCountries"
            name="startingCountries"
            required
            variant="outlined"
          />
          {/* <input
              type="number"
              placeholder="startingCountries"
              name="startingCountries"
              required
            ></input> */}
        </div>
        <div>
          <TextField
            required
            type="number"
            placeholder="interval"
            name="troopInterval"
            variant="outlined"
          />
          {/* <input
              type="number"
              placeholder="interval"
              name="troopInterval"
              required
            ></input> */}
        </div>
        <Button
          type="submit"
          name="submit"
          value="create"
          variant="contained"
          color="secondary"
        >
          Commence WAR
        </Button>
      </form>
      {/* </div>
    </div> */}
    </div>
  );
}

function Video() {
  useEffect(() => {
    attemptPlay();
  }, []);

  const videoEl = useRef(null);

  const attemptPlay = () => {
    videoEl &&
      videoEl.current &&
      videoEl.current.play().catch((error) => {
        console.error("Error attempting to play", error);
      });
  };

  return (
    <div className="video">
      <video muted autoPlay className="home-video" id="home-video">
        <source
          playsInline
          src={videoSource}
          type="video/mp4"
          alt="This is Sparta!"
        />
        What kind of browser version are you on... Your browser unfortunately
