import React, { memo } from 'react';
import { ZoomableGroup, ComposableMap, Geographies, Geography, useZoomPan, Marker } from 'react-simple-maps';
import { geoCentroid } from 'd3-geo';
import ReactTooltip from 'react-tooltip';

const geoUrl = 'https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json';

const rounded = (num) => {
	if (num > 1000000000) {
		return Math.round(num / 100000000) / 10 + 'Bcn';
	} else if (num > 1000000) {
		return Math.round(num / 100000) / 10 + 'M';
	} else {
		return Math.round(num / 100) / 10 + 'K';
	}
};

function notThisCountry(country) {
	const { NAME } = country.properties;
	// return NAME !== '';
	return true;
}

const mapWidth = 800;
const mapHeight = 600;
const MapChart = ({ setTooltipContent }) => {
	return (
		<div>
			<ComposableMap projectionConfig={{ scale: 200 }} width={mapWidth} height={mapHeight}>
				<ZoomableGroup>
					<Geographies geography={geoUrl}>
						{({ geographies }) =>
							geographies.map((geo) => (
								<Geography
									key={geo.rsmKey}
									geography={geo}
									onMouseEnter={() => {
										const { NAME, POP_EST } = geo.properties;
										setTooltipContent(`${NAME} — ${rounded(POP_EST)}`);
									}}
									onMouseLeave={() => {
										setTooltipContent('');
									}}
									style={{
										default: {
											fill: '#D6D6DA',
											outline: 'none'
										},
										hover: {
											fill: '#F53',
											outline: 'none'
										},
										pressed: {
											fill: '#E42',
											outline: 'none'
										}
									}}
								/>
							))}
					</Geographies>
				</ZoomableGroup>
			</ComposableMap>
		</div>
	);
};

function VectorMap() {
	const [ content, setContent ] = React.useState('');
	return (
		<div>
			<MapChart setTooltipContent={setContent} />
			<ReactTooltip>{content}</ReactTooltip>
		</div>
	);
}

export default memo(VectorMap);
