import React, { Component, useState } from "react";
import {
  ComposableMap,
  Geographies,
  Geography,
  ZoomableGroup,
} from "react-simple-maps";
import ReactTooltip from "react-tooltip";
import { useSpring, animated } from "react-spring";
import "./Map.css";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const id = "test";
const socketURL = "ws://localhost:8080/game/" + id + "ws";

class GameMap extends Component {
  socket = new WebSocket(socketURL + id + "ws");
  componentDidMount() {
    this.socket.onopen = () => {
      console.log("Connection Successful");
    };

    this.socket.onmessage = (msg) => {
      const message = JSON.parse(msg.data);
      console.log(msg);
    };

    this.socket.onclose = () => {
      console.log("Disconnected");
    };
  }

  render() {
    return <MapDisplay />;
  }
}

function MapDisplay() {
  return (
    <div>
      <SideBar />
    </div>
  );
}

var country;
var pop_est;
var gdp;
var subrg;
var continent;
var displayCountryDetails = false;

function CountryDetails(props) {
  return (
    <div>
      <h2>Spy Report On {props.Country}:</h2>
      <h3>Population: {props.Population}</h3>
      <h3>GDP: {props.GDP}</h3>
      <h3>Subregion: {props.Subregion}</h3>
      <h3>Continent: {props.Continent}</h3>
    </div>
  );
}

function SideBar() {
  const [state, setState] = useState("");
  const [spydata, setSpydata] = useState({
    Country: "",
    Population: "",
    GDP: "",
    Subregion: "",
    Continent: "",
  });
  return (
    <div>
      <div className="map-sidebar-wrapper">
        <div className="map-sidebar-info-wrapper">
          <h1>Welcome Commander!</h1>
          <p>
            This is your war control room. Help us attain victory over our
            enemies. The Gods are on our side!
          </p>
          <CountryDetails props={spydata} />
        </div>
      </div>
      <MapSettings setTooltipContent={setState} setSpydata={setSpydata} />
      <ReactTooltip>{state}</ReactTooltip>
    </div>
  );
}

const getnum = (num) => {
  var num = Math.round(num);
  if (num > Math.pow(10, 12)) {
    num = num / Math.pow(10, 12) + " Trillion";
  } else if (num > Math.pow(10, 9)) {
    num = num / Math.pow(10, 9) + " Billion";
  } else if (num > Math.pow(10, 6)) {
    num = num / Math.pow(10, 6) + " Million";
  }
  return num;
};

const MapSettings = ({ setTooltipContent, setSpydata }) => {
  return (
    <div className="map-wrapper">
      <ComposableMap data-tip="">
        <ZoomableGroup>
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill="#AAA"
                  stroke="#FFF"
                  onMouseEnter={() => {
                    const {
                      NAME,
                      POP_EST,
                      GDP_MD_EST,
                      SUBREGION,
                      CONTINENT,
                    } = geo.properties;
                    setTooltipContent(
                      `${NAME} - $${getnum(GDP_MD_EST * Math.pow(10, 6))}`
                    );
                    setSpydata(NAME, POP_EST, GDP_MD_EST, SUBREGION, CONTINENT);
                  }}
                  onMouseLeave={() => {
                    setTooltipContent("");
                    setSpydata("", "", "", "", "");
                  }}
                  onClick={() => {
                    const {
                      NAME,
                      POP_EST,
                      GDP_MD_EST,
                      SUBREGION,
                      CONTINENT,
                    } = geo.properties;
                    country = NAME;
                    pop_est = getnum(POP_EST);
                    gdp = getnum(GDP_MD_EST * Math.pow(10, 6));
                    subrg = SUBREGION;
                    continent = CONTINENT;
                    displayCountryDetails = !displayCountryDetails;
                  }}
                  style={{
                    default: {
                      fill: "#D6D6DA",
                      outline: "none",
                    },
                    hover: {
                      fill: "#F53",
                      outline: "none",
                    },
                    pressed: {
                      fill: "#D6D6DA",
                      outline: "none",
                    },
                  }}
                />
              ))
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    </div>
  );
};

export default GameMap;
