import React, { Component } from "react";
import videoSource from "../media/HomeBackgroundVideo.mp4";
import {
  Card,
  Container,
  Paper,
  Typography,
  AppBar,
  Toolbar,
  Box,
  makeStyles,
} from "@material-ui/core";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      {new Date().getFullYear()}
      {"."}
      {"CONQUER V2.0 by Shashwat Kansal and Akshat Tripathi"}
    </Typography>
  );
}

function Title() {
  return (
    <AppBar position="absolute" color="default">
      <Toolbar>
        <Typography variant="h6" color="inherit" align="center">
          CONQUER V2.0
        </Typography>
      </Toolbar>
    </AppBar>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    padding: theme.spacing(2),
  },
}));

export default class Home extends Component {
  render() {
    return (
      <div className="home-page">
        <video loop autoPlay className="home-video">
          <source src={videoSource} type="video/mp4" />
          Your browser unfortunately does not yet support the video tag!
        </video>
        <div></div>
        <div className="title-container">
          <Box>
            <Paper elevation={5} variant="elevation" classname={classes.paper}>
              <Typography component="h1" variant="h4" align="center">
                Join a game now!
              </Typography>
            </Paper>
          </Box>
        </div>
        <div z-index="1">
          <Copyright />
        </div>
      </div>
    );
  }
}
