import React, { useState } from 'react';
import {
	Typography,
	Button,
	TextField,
	Paper,
	Slider,
	DialogContentText,
	makeStyles,
	Grid,
	IconButton
} from '@material-ui/core';
import CssBaseline from '@material-ui/core/CssBaseline';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';
import { fade } from '@material-ui/core/styles/colorManipulator';
import './StartGameBox.css';

//TODO: Add username var here and set accordingly.
var mode = 0;

const useStyles = makeStyles((theme) => ({
	paper: {
		marginLeft: '60%',
		marginRight: '15%',
		marginTop: '10%',
		marginBottom: '20%',
		background: fade('#000000', 0.8),
		color: 'white',
		padding: theme.spacing(3)
	},
	buttons: {
		display: 'flex',
		justifyContent: 'flex-end'
	},
	button: {
		marginTop: theme.spacing(3),
		marginLeft: theme.spacing(1)
	}
}));

function StartGameBox() {
	const classes = useStyles();
	const [ mode, setmode ] = useState(0);

	const handleModeOne = () => {
		setmode(1);
	};

	const handleModeZero = () => {
		setmode(0);
	};

	const handleModeTwo = () => {
		setmode(2);
	};

	const getOptionContent = (mode) => {
		switch (mode) {
			case 0:
				return <StartContent setModeToOne={handleModeOne} setModeToTwo={handleModeTwo} />;
			case 1:
				return <JoinGame setModeToZero={handleModeZero} />;
			case 2:
				return <CreateGame setModeToZero={handleModeZero} />;
			default:
				throw new Error('?? what happened ...');
		}
	};

	return (
		<div>
			<CssBaseline />
			<Paper className={classes.paper} elevation={3}>
				<Grid container spacing={2}>
					<Grid item xs={12}>
						<Typography component="h3" variant="h5" align="center">
							It's time to begin Commander.
						</Typography>
					</Grid>
					{getOptionContent(mode)}
				</Grid>
			</Paper>
		</div>
	);
}

const StartContent = ({ setModeToOne, setModeToTwo }) => {
	return (
		<React.Fragment>
			<Grid item xs={12} sm={6}>
				<div
					style={{
						marginLeft: '10%',
						marginRight: '10%',
						marginTop: '10%',
						marginBottom: '10%',
						textAlign: 'center',
						fontSize: '50'
					}}
				>
					<IconButton aria-label="newgame" color="primary" size="medium" onClick={setModeToTwo}>
						<AddCircleIcon
							style={{
								fontSize: '50'
							}}
						/>
					</IconButton>
					<Typography variant="subtitle2">Create New Game</Typography>
				</div>
			</Grid>
			<Grid item xs={12} sm={6}>
				<div
					style={{
						marginLeft: '10%',
						marginRight: '10%',
						marginTop: '10%',
						marginBottom: '10%',
						textAlign: 'center'
					}}
				>
					<IconButton aria-label="joingame" color="secondary" size="medium" onClick={setModeToOne}>
						<DoubleArrowIcon
							style={{
								fontSize: '50'
							}}
						/>
					</IconButton>
					<Typography variant="subtitle2">Join Game</Typography>
				</div>
			</Grid>
		</React.Fragment>
	);
};

const JoinGame = ({ setModeToZero }) => {
	return (
		<Grid items xs={12}>
			<DialogContentText style={{ color: 'white' }}>Time to create your world war.</DialogContentText>
			<Paper className="gamebox-wrapper" styles={{ height: '50px' }}>
				<h3 className="gamebox-title">Join the game!</h3>

				<form action="/join" method="POST">
					<DialogContentText>Please enter game credentials.</DialogContentText>
					<TextField type="text" id="ign" placeholder="Username" name="username" required />
					<br />
					<TextField type="password" placeholder="Password" name="password" required />
					<br />
					<TextField type="text" placeholder="Game Id" name="id" required />
					<br />
					<Button type="submit" name="submit" value="join" variant="contained" color="primary">
						Join Game
					</Button>
				</form>
			</Paper>
		</Grid>
	);
};

const CreateGame = ({ setModeToZero }) => {
	return (
		<Grid item xs={12}>
			<DialogContentText>Time to create your world war.</DialogContentText>
			<Paper>
				<form action="/create" method="POST">
					<select className="gamemode" name="type" required>
						<option value="realtime">Realtime game</option>
					</select>
					<div>
						<TextField type="text" placeholder="Username" name="username" required variant="outlined" />
					</div>
					<div>
						<TextField type="password" placeholder="Password" name="password" required variant="outlined" />
					</div>
					<div>
						<TextField
							className="noOfPlayers"
							type="number"
							placeholder="maxPlayers"
							name="maxPlayers"
							required
							variant="outlined"
						/>
					</div>
					<div>
						<TextField
							type="number"
							placeholder="startingTroops"
							name="startingTroops"
							required
							variant="outlined"
						/>
					</div>
					<div>
						<TextField
							type="number"
							placeholder="startingCountries"
							name="startingCountries"
							required
							variant="outlined"
						/>
					</div>
					<div>
						<Typography id="troopInterval" gutterBottom>
							Small steps
						</Typography>
						<Slider
							defaultValue={0.00000005}
							aria-labelledby="troopInterval"
							step={1}
							marks
							min={1}
							max={10}
							valueLabelDisplay="auto"
						/>
					</div>
					<Button type="submit" name="submit" value="create" variant="contained" color="secondary">
						Commence WAR
					</Button>
				</form>
			</Paper>
		</Grid>
	);
};

export default StartGameBox;
