import React, { memo } from 'react';
import { ZoomableGroup, ComposableMap, Geographies, Geography, useZoomPan, Marker } from 'react-simple-maps';
import { geoCentroid } from 'd3-geo';
import ReactTooltip from 'react-tooltip';

const geoUrl = 'https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json';

const rounded = (num) => {
	if (num > 1000000000) {
		return Math.round(num / 100000000) / 10 + 'Bcn';
	} else if (num > 1000000) {
		return Math.round(num / 100000) / 10 + 'M';
	} else {
		return Math.round(num / 100) / 10 + 'K';
	}
};

const MapSettings = ({ setTooltipContent }) => {
	return (
		<div className="map-wrapper">
			<ComposableMap>
				<CustomZoomableGroup center={[ 0, 0 ]}>
					{(position) => (
						<div>
							<Geographies geography={geoUrl}>
								{({ geographies }) => (
									<div>
										{geographies.map((geo) => {
											return notThisCountry(geo) ? (
												<Geography
													key={geo.rsmKey}
													geography={geo}
													fill={fillcolour}
													stroke={strokecolour}
													onMouseEnter={() => {
														const {
															NAME,
															POP_EST,
															GDP_MD_EST,
															SUBREGION,
															CONTINENT
														} = geo.properties;

														setTooltipContent(`${NAME} - ENEMY TERRITORY`);
														// setname(NAME);
														// setpop_est(getnum(POP_EST));
														// setgdp(getnum(GDP_MD_EST * Math.pow(10, 6)));
														// setsubrg(SUBREGION);
														// setcontinent(CONTINENT);
														// setdisplay(true);
													}}
													onMouseLeave={() => {
														setTooltipContent('');
														// setdisplay(false);
													}}
													style={{
														default: {
															fill: '#D6D6DA',
															outline: 'none'
														},
														hover: {
															fill: '#F53',
															outline: 'none'
														},
														pressed: {
															fill: '#D6D6DA',
															outline: 'none'
														}
													}}
													onClick={() => {
														const { ISO_A2 } = geo.properties;
														// setclickedCountry(ISO_A2);
													}}
													onDoubleClick={() => {
														// setdoubleClicked();
													}}
												/>
											) : null;
										})}

										{geographies.map((geo) => {
											const centroid = geoCentroid(geo);
											const { ISO_A2 } = geo.properties;
											return (
												<g key={geo.rsmKey}>
													{
														<Marker coordinates={centroid}>
															<text fontSize={7 / position.k} alignmentBaseline="middle">
																{countries[ISO_A2]}
															</text>
														</Marker>
													}
												</g>
											);
										})}
									</div>
								)}
							</Geographies>
						</div>
					)}
				</CustomZoomableGroup>
			</ComposableMap>
		</div>
	);
};

const CustomZoomableGroup = ({ children, ...restProps }) => {
	const { mapRef, transformString, position } = useZoomPan(restProps);
	return (
		// <div className="zoomable-group">
		<g ref={mapRef}>
			<rect fill="transparent" />
			<g transform={transformString}>{children(position)}</g>
		</g>
		// </div>
	);
};

function VectorMap() {
	const [ content, setContent ] = React.useState('');
	return (
		<div>
			<MapChart setTooltipContent={setContent} />
			<ReactTooltip>{content}</ReactTooltip>
		</div>
	);
}

export default memo(VectorMap);
