import React, { Component, useState } from "react";
import {
  ComposableMap,
  Geographies,
  Geography,
  ZoomableGroup,
} from "react-simple-maps";
import ReactTooltip from "react-tooltip";
// import { useSpring, animated } from "react-spring";
import "./Map.css";
import { connect } from "../api/index.js";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

//NOTE: For API, please see src/api/index.js;

class GameMap extends Component {
  constructor() {
    super();
    connect();
    const naam = "Shashwat";
  }

  render() {
    return <SideBar props="Shashwat" />;
  }
}

// function MapDisplay() {
//   return (
//     <div>
//       <SideBar />
//     </div>
//   );
// }

//Variables for each country to display for Sidebar
// var country;
// var pop_est;
// var gdp;
// var subrg;
// var continent;
// var displayCountryDetails = false;
// var troopsInLand;
// var yourland;

function SideBar() {
  //Fetch #troops, attack, move options, fix data vals
  const [state, setState] = useState("");
  const [spydata, setSpydata] = useState({
    name: "",
    pop_est: "",
    gdp: "",
    subrg: "",
    continent: "",
    display: false,
  });
  const [naam, setname] = useState("");
  const [pop_est, setpop_est] = useState("");
  const [gdp, setgdp] = useState("");
  const [subrg, setsubrg] = useState("");
  const [continent, setcontinent] = useState("");
  const [display, setdisplay] = useState("");

  const CountryDetails = () => {
    return (
      <div>
        <h2>Spy Report On {this.naam}:</h2>
        <h3>Population: {this.population}</h3>
        <h3>GDP (PPP): {this.gdp}</h3>
        <h3>Subregion: {this.subrg}</h3>
        <h3>Continent: {this.continent}</h3>
      </div>
    );
  };

  return (
    <div>
      <div className="map-sidebar-wrapper">
        <div className="map-sidebar-info-wrapper">
          <div>
            <h1>Welcome Commander of {naam}!</h1>
          </div>
          <p>
            This is your war control room. Help us attain victory over our
            enemies. The Gods are on our side!
          </p>
          {spydata.display && <CountryDetails />}
        </div>
      </div>
      <MapSettings
        setTooltipContent={setState}
        setSpydata={setSpydata}
        setname={setname}
      />
      <ReactTooltip>{state}</ReactTooltip>
    </div>
  );
}

const getnum = (num) => {
  var num = Math.round(num);
  if (num > Math.pow(10, 12)) {
    num = num / Math.pow(10, 12) + " Trillion";
  } else if (num > Math.pow(10, 9)) {
    num = num / Math.pow(10, 9) + " Billion";
  } else if (num > Math.pow(10, 6)) {
    num = num / Math.pow(10, 6) + " Million";
  }
  return num;
};

const MapSettings = ({
  setTooltipContent,
  setname,
  setpop_est,
  setsubrg,
  setcontinent,
  setgdp,
}) => {
  return (
    <div className="map-wrapper">
      <ComposableMap data-tip="">
        <ZoomableGroup>
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill="#AAA"
                  stroke="#FFF"
                  onMouseEnter={() => {
                    const {
                      NAME,
                      POP_EST,
                      GDP_MD_EST,
                      SUBREGION,
                      CONTINENT,
                    } = geo.properties;
                    setTooltipContent(
                      `${NAME} - $${getnum(GDP_MD_EST * Math.pow(10, 6))}`
                    );
                    setname(NAME);
                    setpop_est(getnum(POP_EST));
                    setgdp(getnum(GDP_MD_EST * Math.pow(10, 6)));
                    setsubrg(SUBREGION);
                    setcontinent(CONTINENT);
                    setdisplay(true);
                    setname(name);
                  }}
                  onMouseLeave={() => {
                    setTooltipContent("");
                    setdisplay(false);
                  }}
                  style={{
                    default: {
                      fill: "#D6D6DA",
                      outline: "none",
                    },
                    hover: {
                      fill: "#F53",
                      outline: "none",
                    },
                    pressed: {
                      fill: "#D6D6DA",
                      outline: "none",
                    },
                  }}
                />
              ))
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    </div>
  );
};

export default GameMap;
