import React, { Component, useEffect, useRef } from "react";
import videoSource from "../media/HomeBackgroundVideo.mp4";
import {
  Card,
  Container,
  Paper,
  Typography,
  AppBar,
  Toolbar,
  Box,
  makeStyles,
} from "@material-ui/core";
import "./Home.css";

function Credits() {
  return (
    <div className="credits">
      <Paper position="absolute" color>
        <Typography variant="body2" color="gray" align="center">
          {"Copyright © "}
          {new Date().getFullYear()}
          {"."}
        </Typography>
      </Paper>
    </div>
  );
}

function Title() {
  return (
    <div className="title-head">
      <Paper elevation={24} boxShadow={3} variant="outlined" color="blue">
        <Box align="center" boxShadow={1} color="blue">
          CONQUER V2.0
        </Box>
        <Box>
          <Typography variant="subtitle1" color="inherit" align="center">
            by Shashwat Kansal and Akshat Tripathi
          </Typography>
        </Box>
      </Paper>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  title: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    padding: theme.spacing(2),
  },
}));

function StartGameBox() {
  const classes = useStyles();

  return (
    <div className="gamebox-container">
      <Box>
        <Paper
          elevation={5}
          variant="elevation"
          classname={classes.title}
        ></Paper>
      </Box>
    </div>
  );
}

function Video() {
  useEffect(() => {
    attemptPlay();
  }, []);

  const videoEl = useRef(null);

  const attemptPlay = () => {
    videoEl &&
      videoEl.current &&
      videoEl.current.play().catch((error) => {
        console.error("Error attempting to play", error);
      });
  };

  return (
    <div className="video">
      <video loop muted autoPlay className="home-video">
        <source
          playsInline
          src={videoSource}
          type="video/mp4"
          alt="This is Sparta!"
        />
        What kind of browser version are you on... Your browser unfortunately
        does not yet support the video tag!
      </video>
    </div>
  );
}

export default class Home extends Component {
  render() {
    return (
      <div className="home-page">
        <div>
          <header>Conquer V2.0</header>
        </div>
        <Video />
        <Title />
        {/* <StartGameBox /> */}
        <Credits />
      </div>
    );
  }
}
